import React, { Component } from 'react';
import './css/extra.css';
import ExtraComp from './addComp/extraAdd';
import uniqid from "uniqid";


class Extra extends Component {
    constructor() {
        super();

        this.state = {
            skill: {
                id: uniqid(),
                desc: "Team-working",

            },
            skillArray: [],
            interest: {
                id: uniqid(),
                desc: "Reading",

            },
            interestArray: [],
        }
    }

    handleNewSkill = (e) => {
        e.preventDefault();
        this.setState({
            edu: {
                id: this.state.edu.id,
                title: 'University of London',
                desc: "Bachelors's in Software Engineering",
                since: "2017",
                to: '2020',
                where: "London, UK"
            },
        });
        this.setState({
            eduArray: this.state.eduArray.concat(this.state.edu),
        });
    };

    render() {
        const { skillArray, interestArray } = this.state;

        return (
            <div id="extra">

                <div id="skills">
                    <div id="headingwrap">
                        <div id="heading">SKILLS</div>
                        <button id="addExtraSkill">+</button>
                    </div>

                    <ExtraComp desc='Team-working'></ExtraComp>

                </div>

                <div id="interests">
                    <div id="headingwrap">
                        <div id="heading">INTERESTS</div>
                        <button id="addExtraInterest">+</button>
                    </div>

                    <ExtraComp desc="music"></ExtraComp>
                </div>



            </div>
        )
    }
}

export default Extra
