import React, { Component } from 'react';
import './css/extra.css';


class Extra extends Component {
    constructor() {
        super();

        this.state = {
            interest: 'Music',
            skill: 'Communication'
        }
    }

    render() {
        const { interest, skill } = this.state;

        return (
            <div id="extra">

                <div id="headingwrap">
                    <div id="heading">SKILLS</div>
                    <button id="addExtra">+</button>
                </div>

                <div className="flexwrap">
                    <div className="wrapperEdu">
                        <div className="desc" contentEditable="true">{skill}</div>
                    </div>
                </div>

                <div id="headingwrap">
                    <div id="heading">INTERESTS</div>
                    <button id="addExtra">+</button>
                </div>

                <div className="flexwrap">
                    <div className="wrapperEdu">
                        <div className="desc" contentEditable="true">{interest}</div>
                    </div>
                </div>


            </div>
        )
    }
}

export default Extra
