import React, { Component } from 'react';
import './css/general.css';


class General extends Component {
    constructor() {
        super();

        this.state = {
            name: 'John Smith',
            title: 'Software Engineer',
            email: 'john.smith@gmail.com',
            phone: '+39 8473847298',
            address: 'London, UK',
            linkedin: 'linkedin.com/in/username'
        }
    }

    render() {
        const { name, email, phone, address, linkedin, title } = this.state;

        return (
            <div id="general">
                <div className="bigwrapper">
                    <div className="wrapper">
                        <img src={require("./img/pp.png")} id="pp" alt="your profile pic"></img>
                    </div>
                    <div className="smallwrapper">
                        <div className="wrapper">
                            <div className="value" contentEditable="true" id="name">{name}</div>
                        </div>
                        <div className="wrapper">
                            <div className="value" contentEditable="true" id="title">{title}</div>
                        </div>
                    </div>
                </div>
                <div className="bigwrapper about">
                    <div id="aboutme">ABOUT ME</div>

                </div>
            </div>
        )
    }
}

export default General
