import React, { Component } from 'react';
import './css/experience.css';
import ExperienceComp from './addComp/experienceAdd';
import uniqid from "uniqid";


class Experience extends Component {
    constructor() {
        super();

        this.state = {
            exp: {
                id: uniqid(),
                title: 'Software Engineer',
                desc: "at Google, working with React",
                since: "2020",
                to: '2022',
                where: 'London, UK'
            },
            expArray: [],
        }
    }

    handleNewExp = (e) => {
        e.preventDefault();
        this.setState({
            edu: {
                id: this.state.edu.id,
                title: 'University of London',
                desc: "Bachelors's in Software Engineering",
                since: "2017",
                to: '2020',
                where: "London, UK"
            },
        });
        this.setState({
            eduArray: this.state.eduArray.concat(this.state.edu),
        });
    };

    render() {

        return (
            <div id="experience">
                <div id="headingwrap">
                    <div id="heading">WORK EXPERIENCE</div>
                    <button id="addExp">+</button>
                </div>

            </div>
        )
    }
}

export default Experience
