import React, { Component } from 'react';
import './education.css';


class Education extends Component {
    constructor() {
        super();

        this.state = {
            title: 'University of London',
            desc: 'Degree in Software Engineering',
            since: '2020',
            to: '2022',
            where: 'London, UK'
        }
    }

    render() {
        const { title, desc, since, to, where } = this.state;

        return (
            <div id="education">
                <div id="heading">EDUCATION</div>
                <div className="flexwrap">
                    <div className="wrapperEdu">
                        <div className="title" contentEditable="true">{title}</div>
                        <div className="desc" contentEditable="true">{desc}</div>
                    </div>
                    <div className="wrapperEdu">
                        <div className="title time" contentEditable="true">{since}-{to}</div>
                        <div className="desc" contentEditable="true">{where}</div>
                    </div>
                </div>

            </div>
        )
    }
}

export default Education
