import React, { Component } from 'react';
import '../css/extra.css';


class ExtraComp extends Component {
    constructor() {
        super();

        this.state = {
            desc: 'Misuc',
        }
    }

    render() {
        const { desc } = this.state;

        return (
            <div className="flexwrap">
                <div className="wrapperEdu">
                    <div className="desc" contentEditable="true">{desc}</div>
                </div>
            </div>
        )
    }
}

export default ExtraComp
