import React, { Component } from 'react';
import './css/extra.css';
import ExtraComp from './addComp/extraAdd';
import uniqid from "uniqid";
import ExtraSkill from './addComp/extraAdd';


class Extra extends Component {
    constructor() {
        super();

        this.state = {
            skill: {
                id: uniqid(),
                desc: "Team-working",

            },
            skillArray: [],
            interest: {
                id: uniqid(),
                desc: "Reading",

            },
            interestArray: [],
        }
    }

    handleNewSkill = (e) => {
        e.preventDefault();
        this.setState({
            skill: {
                id: this.state.skill.id,
                desc: "Team-working",
            },
        });
        this.setState({
            skillArray: this.state.skillArray.concat(this.state.skill),
        });
    };

    handleNewInterest = (e) => {
        e.preventDefault();
        this.setState({
            interest: {
                id: this.state.interest.id,
                desc: "Reading",
            },
        });
        this.setState({
            interestArray: this.state.interestArray.concat(this.state.interest),
        });
    };

    render() {
        const { skillArray, interestArray } = this.state;

        return (
            <div id="extra">

                <div id="skills">
                    <div id="headingwrap">
                        <div id="heading">SKILLS</div>
                        <button id="addExtraSkill" onClick = {this.handleNewSkill}>+</button>
                    </div>

                    <ExtraSkill skillArray={skillArray}></ExtraSkill>

                </div>

                <div id="interests">
                    <div id="headingwrap">
                        <div id="heading">INTERESTS</div>
                        <button id="addExtraInterest" onClick = {this.handleNewInterest}>+</button>
                    </div>


                </div>



            </div>
        )
    }
}

export default Extra
